import RightArrow from './RightArrow';


const Slider = () => {
    const [data, setData] = useState({photos: [] });
    const [count, setCount] = useState(0);

    useEffect(() => {
      const fetchData = async () => {
        const result = await axios(
          'http://localhost:5001',
        );
        setData(result.data);
      };
      fetchData();
    }, []);
    return (
      <div className="slider">
      <Slide >    
            <LeftArrow />
